  }
  .SearchResults .Meta {
    width: 29%;
    display: inline-block;
    vertical-align: top;
    padding-top: 0
  }
  .SearchResults .MItem {
    white-space: normal;
    display: inline-block;
    line-height: 1.1;
    vertical-align: top
  }
  .SearchResults .MItem:nth-child(1) {
    width: calc(100% - 19ex)
  }
  .SearchResults .MItem:nth-child(2) {
    display: none
  }
  .SearchResults .MItem:nth-child(3) {
    width: 15ex
  }
  .Activities .ItemContent {
    margin-left: calc(50px - 2ex)
  }
  .DataList .Activity {
    padding-left: 2ex;
    padding-right: 1ex
  }
  .Activity .Title {
    width: calc(100% - (20ex * 10) / 11);
    font-size: 11pt
  }
  .Activity .Title a {
    font-size: 11pt
  }
  .Activity .Excerpt {
    width: calc(100% - 21.5ex);
    font-size: 10pt;
    display: inline-block
  }
  .Activity .Meta {
    width: 20ex;
    font-size: 10pt;
    text-align: right
  }
  .Activity .Meta .DateCreated {
    font-size: 10pt
  }

/* FORUM CATEGORIES *******************************************************************/
  .CategoryList {
    padding: 4ex 0 0 0;
