  .NarrowList .Item {
    padding-left: 3ex
  }
  .Category {
    padding: 1em 0
  }
  .TitleWrap {
    display: inline-block;
    width: 20%;
    vertical-align: top;
    margin-top: -0.3ex;
  }
  .CategoryDescription {
    display: inline-block;
    width: 20%;
    vertical-align: top;
    line-height: 1.2;
    font-size: 10pt;
  }
  .Meta {
    display: inline-block;
    width: calc(60% - 32px);
    line-height: 1.2;
    vertical-align: top;
  }
  .MItem {
    vertical-align: top;
    display: inline-block;
    white-space: normal
  }
  .DiscussionCount {
    width: 12ex
  }
  .CommentCount {
    width: 12ex
  }
  .LastDiscussionTitle {
    width: calc(100% - 55ex)
  }
  .LastCommentDate {
    width: 15ex
  }

#Content h1.H {
