	color: var(--m-light-60) !important;
}

.recommend_outtest_container .j_add_tag:focus {
	background: hsla(0,0%,var(--m-black-lightness),calc(0.1*var(--m-block-darken) + 0.04)) !important;
	border-bottom: 4px solid rgba(0,0,0,.1) !important;
}

.recommend_outtest_container .recommend_tag {
	margin-top: 12px !important;
	padding: 0 6px !important;
	text-align: left;
}

.recommend_outtest_container .recommend_tag:empty {
	display: none;
}

.recommend_outtest_container .pb_recommend_tag {
	margin: 0 !important;
	margin-bottom: 4px !important;
	margin-right: 5px !important;
	padding: 0 6px !important;
	height: 20px;
	line-height: 20px;
	float: none;
	display: inline-block;
	background: var(--m-block-b010);
	border: none;
