}

.recommend_outtest_container .pb_recommend_tag span {
	position: static !important;
	top: auto !important;
}

.recommend_outtest_container .pb_recommend_tag:hover {
	background: hsla(0,0%,var(--m-black-lightness),calc(0.1*var(--m-block-darken) + 0.1)) !important;
}

.recommend_outtest_container .tag_button {
	margin-top: 12px !important;
	margin-bottom: 16px !important;
}

.recommend_outtest_container .tag_submit_button {
	width: 200px;
	height: 27px;
	line-height: 27px;
	display: block;
	position: relative;
	margin: 0 auto;
	z-index: 1;
	text-align: center;
