/*【悬停显示贴子列表页会员标识和非实名认证印记】*/
.frs_bright_preicon,
.frs_bright_icons>*:not([data-name="user_type"]):not([data-name="is_verify"]) {
	opacity: 0;
	transition: opacity .4s ease;
}

.threadlist_bright .j_thread_list:hover .frs_bright_preicon,
.threadlist_bright .j_thread_list:hover .frs_bright_icons>*:not([data-name="user_type"]):not([data-name="is_verify"]) {
	opacity: 1;
}

.j_thread_list .red_text,
.j_thread_list .red-text,
.j_thread_list .vip_red,
.j_thread_list .vip-red,
.j_thread_list .vip_red:hover,
.j_thread_list .vip-red:hover,
.j_thread_list .vip_red:visited,
.j_thread_list .vip-red:visited {
	color: inherit !important;
}

.j_thread_list .sign_highlight {
	color: inherit !important;
}

.threadlist_bright .j_thread_list:hover .sign_highlight {
	color: #ffa640 !important;
}

.threadlist_bright .j_thread_list:hover .red_text,
.threadlist_bright .j_thread_list:hover .red-text,
.threadlist_bright .j_thread_list:hover .vip_red,
.threadlist_bright .j_thread_list:hover .vip-red {
	color: #f74d4a !important;
}

/*【帖子列表页回复数垂直对齐整块列表项】*/
.threadlist_li_left,
.j_threadlist_li_left {
	align-items: center;
}

/*【去除底部空白高度】*/
.wrap2 {
	padding: 0 !important;
}

/*【自动跳转吧提示】*/
.polysemant-redirect-section {
	border-radius: 10px;
	background-color: var(--m-ecolor);
	color: #fff;
	border:none !important;
}

.polysemant-redirect-section *{
	color: inherit !important;
}

.polysemant-redirect-section>.close-button{
	font-size: 0 !important;
	position: absolute;
	top: 0;
	right: 10px;
	height: 100%;
	width: 24px !important;
	margin: 0 !important;
	background: none !important;
}

.polysemant-redirect-section>.close-button:before {
	content: "\e5c9";
	font-family: "Material Icons";
	font-size: 24px;
	display: block;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	-moz-transform: translate(-50%, -40%);
}

/*【贴吧页内容域】*/
.forum_content {
	display: flex;
}

/*【贴吧页列表块】*/
#contet_wrap,
#content_wrap {
	flex: 1;
	box-sizing: border-box;
	float: none !important;
	display: block;
	width: 0 !important;
}

#content_leftList,
div[id="pagelet_frs-list/pagelet/thread_list"],
#thread_list,
.j_thread_list,
.threadlist_bright .t_con {
	width: 100% !important;
	box-sizing: border-box;
	padding: 0 !important;
	margin: 0 !important;
	border: none !important;
}

.threadlist_bright>li.thread_top_list_folder{
	background: none !important;
}

.interview .threadListGroupCnt,
.thread_top_list>li,
.threadlist_bright>li{
	border-bottom: 1px solid var(--m-line-d006) !important;
	transition: background .4s ease;
	position: relative;
}

.thread_top_list>li:last-of-type,
.threadlist_bright>li:last-of-type{
	border-bottom: none !important;
}

.thread_top_list>li:hover,
.threadlist_bright>li:hover{
	background: var(--m-block-d002);
}

.threadlist_media>li{
	height: auto !important;
	border: none !important;
}

.threadlist_bright .thread_pic_bright,
.threadlist_bright .t_con {
	padding: 12px 0 !important;
	display: flex;
	align-items: stretch;
}

.threadlist_li_left,
.j_threadlist_li_left {
	float: none !important;
	width: 8% !important;
	min-width: 75px !important;
	padding: 0 14px 0 10px !important;
	box-sizing: border-box;
	margin-top: -3px !important;
	position: relative;
	display: flex;
}

.threadlist_rep_num {
	background: var(--m-block-d004) !important;
	overflow: hidden;
	width: 100% !important;
	height: 24px !important;
	line-height: 24px !important;
