	color: var(--m-light-40);
	padding: 0 !important;
	margin: 0 !important;
	border-radius: 12px;
}

.threadlist_li_right,
.j_threadlist_li_right {
	margin: 0 !important;
	float: none !important;
	width: 0 !important;
	flex: 1;
	position: static !important;
}

.threadlist_bright .threadlist_lz,
.threadlist_bright .threadlist_detail {
	width: 100% !important;
	display: flex;
	padding: 0 !important;
}

.threadlist_bright .threadlist_lz {
	overflow: visible;
}

.threadlist_bright .threadlist_detail {
	padding-top: 4px !important;
}

.threadlist_bright .threadlist_title,
.threadlist_bright .threadlist_text {
	float: none !important;
	width: 0 !important;
	flex: 1;
	display: flex;
