**************************************************************************/
/*各种变量*/
:root{
    --appdisplay:none;
    --footdisplay:none;
    --blurdisplay:block;
    --bgcolor:#D9D6E1;/*背景颜色，背景图片无法加载的时候的背景*/
    --bgimg:url(http://wx2.sinaimg.cn/large/005DxcwUgy1g4u2r0rh1kj30j60sjgos.jpg);
    --bgmode:contain;
    --textshadow:#FFFFFF;
    --textshadow2:#EEEEEE;
    --boxbacknone:transparent;/*透明色*/
    --boxbackt:#FFFFFF19;/*元件几乎透明的背景色 */
    --boxback:#FFFFFF66;/*元件半透明的背景色 */
    --boxbacks:#FFFFFFd9;/*元件几乎不透明的背景色 */
    --bartxt:#FFFFFFe6;/*进度条下方文字 */
    --barimg:#FFFFFFdd;/*进度条下方图标 */
    --barnew:#FFFFFF4d;/*进度条已经缓冲部分 */
    --bar:#FFFFFF33;/*进度条 */
    --barold:#00A1D6;/*进度条已经播放部分*/
    --v1:cubic-bezier(0.215, 0.61, 0.355, 1);/*速度模式1*/
}
.bili-footer,
.international-footer,
.link-footer,.link-footer-ctnr{
    display:var(--footdisplay,none) !important;
}/*页面底端*/
ul li.shouji,
.mobile-link-l,
.nav-menu .nav-con .nav-item.mobile,
a[href="//app.bilibili.com"]{
    display:var(--appdisplay,none) !important;
}/*去掉下载APP按钮*/
.nav-menu .blur-bg{
    display:var(--blurdisplay,none) !important;
}/*顶栏模糊*/
/**/
html:not([stylus-iframe]) body{
    background-image:var(--bgimg) !important; /*把背景设置为老婆*/
    background: no-repeat fixed top right;
    background-size:var(--bgmode);
    background-color: var(--bgcolor,#D9D6E1);
    text-shadow: 0px 0px 5px var(--textshadow,#ffffff),0px 0px 8px var(--textshadow2,#eeeeee);
}/**/
