/*分辨率适配*/
.cinema-home-wrapper,
.cinema-documentary-wrapper,
.cinema-movie-wrapper,
.cinema-tv-wrapper,
.index-entry-wrapper{
    width:1180px;/*这个可能会导致低分辨率电脑显示不正常*/
}
@media screen and (max-width: 1440px){
    .cinema-home-wrapper,
    .cinema-documentary-wrapper,
    .cinema-movie-wrapper,
    .cinema-tv-wrapper,
    .index-entry-wrapper{
    width: 1040px;
}  
}
.nominate-m.clearfix.report-wrap-module.report-scroll-module,
.energy-m.clearfix.report-wrap-module.report-scroll-module,
.promote-m.clearfix.report-wrap-module.report-scroll-module,
.sub-zone-m.clearfix.report-wrap-module.report-scroll-module,/*这四个是分页的*/
.zone-module.report-wrap-module.report-scroll-module,/*这个是很多页都有的*/
.area-module,/*纪录片页面*/
.block-area,
.cinema-home-wrapper>.carousel-wrapper,
.index-entry-wrapper,/*这三个是番剧/国创页的*/
.cinema-documentary-wrapper>.carousel-wrapper,
.cinema-movie-wrapper>.carousel-wrapper,
.cinema-tv-wrapper>.carousel-wrapper
{
    transition: background 0.4s, box-shadow 0.3s;
    background: var(--boxback,rgba(255, 255, 255, 0.4));
    box-shadow: 5px 5px 10px 5px rgba(0, 0, 0, .1);
    border-radius: 5px;
    box-sizing: border-box;
    padding:15px 10px 8px 10px;/*左10，下8*/
    margin-top:15px;/*分区之间间隔15*/
    margin-bottom:8px;
    width:100%;
    position:relative;
    left:-20px;
}
.nominate-m.clearfix.report-wrap-module.report-scroll-module{
    height:260px;
}
.promote-m.clearfix.report-wrap-module.report-scroll-module:hover,
.nominate-m.clearfix.report-wrap-module.report-scroll-module:hover,
.energy-m.clearfix.report-wrap-module.report-scroll-module:hover,
.sub-zone-m.clearfix.report-wrap-module.report-scroll-module:hover,/*这四个是分页的*/
.zone-module.report-wrap-module.report-scroll-module:hover,/*这个是很多页都有的*/
