.area-module:hover,/*纪录片页面*/
.block-area:hover,
.cinema-home-wrapper>.carousel-wrapper:hover,
.index-entry-wrapper:hover/*这两个是番剧/国创页的*/
{
    background: var(--boxbacks,rgba(255, 255, 255, 0.85));
